mapper.select:
select *
from user
where username = #{username}
  and password = #{password};

select keyid
from household
where name = #{name}
  and address = #{address};

select *
from household
where name like concat('%', #{name}, '%');

select *
from household
where household.address like concat('%', #{address}, '%');

select *
from household
where household.email like concat('%', #{email}, '%');

select *
from household
where phone like concat('%', #{phone}, '%');
